import React from 'react';
import { BrowserRouter, Link, Route } from 'react-router-dom';
import ApplyForm from './ApplyForm'
class FormRouting extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

        }
    }

    handleInputChanges(event) {

        var changedValue = event.target.value;

        if(event.target.className === "userName") {
            this.setState({
                userName: changedValue
            });
        } else if (event.target.className === "userAge") {
            this.setState({
                userAge: changedValue
            });
        } else if (event.target.className === "userDesignation") {
            this.setState({
                designation: changedValue
            });
        } else {
            this.setState({
                gender: changedValue
            });
        }

    }
    render() {
        return (
            <BrowserRouter>
                <div>
                    <header>
                        <div style={{ backgroundColor: "Yellow" }}>
                            <h2><center>Campus Drive</center></h2></div>

                        <nav style={{ paddingBottom: "20px" }}>
                            <Link style={{ paddingRight: "10px" }} to="/">Home</Link>
                            <Link style={{ paddingRight: "10px" }} to="/apply_form">Apply Here</Link>
                        </nav>
                    </header>
                    <b style={{ marginBottom: "10px;" }}></b>
                    <Route exact path="/" component={Home}></Route>
                    <Route exact path="/apply_form" component={ApplyForm}></Route>
                </div>
            </BrowserRouter>
        )
    }
}


function Home() {
    return (<div>
        Hello,Everyone !!! WELCOME
</div>)
}


