import React from 'react';
class ApplyForm extends React.Component {
    constructor() {
        super();
        this.state = {
            name: '',
            result: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    reponsemessage = () => {
        this.setState({ result: "Your Form has been successfully submitted" });
        this.props.history.push("path/to/push");
        //Use Closure
        setTimeout(() => { this.setState({ result: "" }); }, 3000);
    }
    handleChange(event) {
        //this.setState({ value: event.target.value });
        var changedValue = event.target.value;

        if (event.target.className === "name") {
            this.setState({
                name: changedValue
            });
        } else if (event.target.className === "roll") {
            this.setState({
                roll: changedValue
            });
        } else if (event.target.className === "course") {
            this.setState({
                course: changedValue
            });
        }
        else if (event.target.className === "college") {
            this.setState({
                college: changedValue
            });
        }
        else if (event.target.className === "radio") {
            this.setState({
                radio: changedValue
            });
        } else {
            this.setState({
                address: changedValue
            });
        }
    }
    handleSubmit(event) {
        alert('Registration of ' + this.state.name + ' is successfully submitted.');
        event.preventDefault();
    }
    render() {
        return (
            <div>
                <div style={{ backgroundColor: "Yellow" }}><h3><b><center>Registration Form</center></b></h3></div>
                <form onSubmit={this.handleSubmit}>
                    <label><b>Name&nbsp;</b></label>
                    <input type="text" className="name" value={this.state.name} onChange={this.handleChange} /><br /><br />
                    <label><b>Roll&nbsp;</b></label>
                    <input type="text" className="roll" value={this.state.roll} onChange={this.handleChange} /><br /><br />
                    <label><b>Course&nbsp;</b></label>
                    <select name="course" className="course" value={this.state.course} onChange={this.handleChange}>
                        <option value="">B.Tech</option>
                        <option value="">M.Tech</option>
                        <option value="">BCA</option>
                        <option value="">MCA</option>
                        <option value="">MBA</option>
                    </select><br /><br />
                    <label><b>College&nbsp;</b></label>
                    <select name="college" className="college" value={this.state.college} onChange={this.handleChange}>
                        <option value="">B.Galgotia University</option>
                        <option value="">G.L Bajaj College of Engineering</option>
                        <option value="">GNIOT Group of Institution</option>
                        <option value="">IIMS College</option>
                        <option value="">Sharda University</option>
                    </select><br /><br />
                    <label><b>Branch</b><br />
                        <div className="radio" value={this.state.radio} onChange={this.handleChange}>
                            <label>
                                <input type="radio" value="" checked={true} />
                                CSc.
                        </label>
                            <label>
                                <input type="radio" value="" />
                                EC
                        </label>
                            <label>
                                <input type="radio" value="" />
                                IT
                        </label>
                            <label>
                                <input type="radio" value="" />
                                Civil
                        </label>
                            <label>
                                <input type="radio" value="" />
                                EE
                        </label>
                        </div>
                    </label><br />
                    <label><b>Address</b><br/>
                        <textarea className="address" value={this.state.address} onChange={this.handleChange} />
                    </label><br /><br/>
                    <input type="submit" name="submit" onClick={this.reponsemessage} />
                    <div><b>{this.state.result}</b></div>
                </form>
            </div>
        );
    }
}

export default ApplyForm;
